    public static int maximumGap(final List<Integer> a) {
        int max_diff = -1;
        for (int i = 0; i < a.size(); i++) {
            for (int j = a.size() - 1; j >= 0; j--) {
                if (a.get(j) >= a.get(i)) {
                    if (j - i > max_diff)
                        max_diff = j - i;
                    break;
                }
            }
        }
        return max_diff;
    }

    public static int maximumGap(final List<Integer> a, int p, int q) {
        int max_diff = -1;
        for (int i = p, j = q; i < a.size() && j >= 0; ) {
            if (a.get(j) >= a.get(i)) {
                System.out.println("i: " + i + " j: " + j);
                max_diff = j - i;
                return max_diff;
            } else if (i + 1 < a.size() && a.get(i + 1) <= a.get(j))
                i++;
            else if (j - 1 >= 0 && a.get(j - 1) >= a.get(i))
                j--;
            else if (j - i >= max_diff) {
                int m2 = maximumGap(a, i, j - 1);
                int m1 = maximumGap(a, i + 1, j);

                int diff = m1 > m2 ? m1 : m2;
                if (diff > max_diff)
                    max_diff = diff;
                i++;
                j--;
            } else {
                j--;
                i++;
            }
        }
        return max_diff;
    }
